    grid-template-columns: 1fr 1fr;
    grid-template-rows: 1fr;
    grid-column-gap: 0px;
    grid-row-gap: 0px;
    max-width: 922px;
    margin: auto;
    height: 44px;
`

const Logo = styled.img`
    padding-left: 8px;
    grid-area: 1 / 1;
    width:24px;
`

const AddButton = styled.button`
    margin-right: 15px;
    font-size: 30px;
    background:transparent;
    border: transparent;
    color: white;
    cursor: pointer;
`

