import React, { createContext, useContext, useState } from 'react';

export const SelectedTaskContext = createContext();
export const SelectedTaskProvider = ({ children }) => {
  const [selectedTask, setSelectedTask] = useState('OFFICE');

  return (
    <SelectedTaskContext.Provider
      value={{ selectedTask, setSelectedTask }}
    >
      {children}
    </SelectedTaskContext.Provider>
  );
};

const UseSelectedTaskValue = () => useContext(SelectedTaskContext);
export default UseSelectedTaskValue;
