import React, { useState } from 'react';
import styled from 'styled-components'
import {
  FaChevronDown,
  FaInbox,
  FaRegCalendarAlt,
  FaRegCalendar,
} from 'react-icons/fa';
// import { Projects } from '../Projects';
// import { useSelectedProjectValue } from '../../context';
// import { AddProject } from '../AddProject';
const SidebarWrapper = styled.div`
    @include no-select();
    width: 266px;
    height: calc(100vh);
    padding-top: 74px;
    // position: fixed;
    overflow-x: hidden;
    overflow-y: hidden;
    border-right: 1px solid #f1f1f1;
    background-color: #fafafa;
`

const Styledul = styled.ul`
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  flex-direction:column;
`
const Styledli = styled.li`
    list-style-type: none;
    cursor: pointer;
    // width: 30px;
    // height: 30px;
    text-align: center;
    vertical-align: middle;
    float: left;
  }
`
const Stylediv = styled.div`
  display:flex;
  padding: 10px 0 10px 10px;
  width: 100%;
  gap:0.5rem;
`
const ProjectsDropdown =  styled.div`
    padding: 5px 0;
    padding-left: 10px;
    padding-right: 15px;
    width: 100%;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: center;
        -ms-flex-align: center;
            align-items: center;
    gap:0.5rem;
`
const Sidebar = () => {
//   const { setSelectedProject } = useSelectedProjectValue();
//   const [active, setActive] = useState('inbox');
//   const [showProjects, setShowProjects] = useState(true);

  return (
    <SidebarWrapper>
      <Styledul>
        <Styledli>
          <Stylediv>
            <span>
              <FaInbox />
            </span>
            <span>Inbox</span>
          </Stylediv>
        </Styledli>
        <Styledli>
          <Stylediv>
            <span>
              <FaRegCalendar />
            </span>
            <span>Today</span>
          </Stylediv>
        </Styledli>
        <Styledli>
          <Stylediv>
            <span>
              <FaRegCalendarAlt />
            </span>
            <span>Next 7 days</span>
          </Stylediv>
        </Styledli>
      </Styledul>
    <ProjectsDropdown
        role="button"
        tabIndex={0}
      >
        <span>
          <FaChevronDown
          />
        </span>
        <h2>Projects</h2>
    </ProjectsDropdown>
    </SidebarWrapper>
  );
};

export default Sidebar;
